import axios from 'axios';

// Создаем экземпляр axios с базовыми настройками
const apiClient = axios.create({
  baseURL: 'http://localhost:8000', // Убедитесь, что это правильный URL вашего бэкенда
  withCredentials: false,
  headers: {
    Accept: 'application/json',
    'Content-Type': 'application/json',
  },
});

export default {
  /**
   * Регистрация пользователя
   * @param {Object} user - Данные пользователя (username, email, password)
   * @returns {Promise} - Ответ от сервера
   */
  register(user) {
    return apiClient.post('/register', user);
  },

  /**
   * Авторизация пользователя
   * @param {Object} user - Данные пользователя (username, password)
   * @returns {Promise} - Ответ от сервера
   */
  login(user) {
    return apiClient.post('/token', user);
  },

  /**
   * Получение информации о текущем пользователе
   * @param {string} token - Токен авторизации
   * @returns {Promise} - Ответ от сервера
   */
  getUserInfo(token) {
    return apiClient.get('/users/me', {
      headers: {
        Authorization: `Bearer ${token}`, // Передаем токен в заголовке
      },
    });
  },

  /**
   * Обновление информации о пользователе
   * @param {string} token - Токен авторизации
   * @param {Object} userData - Данные для обновления (username, email, avatar и т.д.)
   * @returns {Promise} - Ответ от сервера
   */
  updateUserInfo(token, userData) {
    return apiClient.put('/users/me/update', userData, {
      headers: {
        Authorization: `Bearer ${token}`, // Передаем токен в заголовке
      },
    });
  },
};
